sys_variable_value.value:
(function execute(inputs, outputs) {
    var reqArr = [];
    var obj = {};
  	var contents = inputs.fileContent.contents;
  	// var sog = new SNC.ScriptableOrderGuide(inputs.order_guide.getUniqueValue());
    var sog = sn_sc.OrderGuide(inputs.order_guide.getUniqueValue());

	try {
      for ( var content in contents) {
      	var json = {};
        json =  contents[content];
        json["opened_by"] = inputs.opened_by.getUniqueValue();

        if (contents["requested_for"] == null) {
    		json["requested_for"] = inputs.opened_by.getUniqueValue();
        }

        var result = sog.process(JSON.stringify(json));

        if (!result) {
          gs.info("Processing the scriptable order guide failed with message: " + sog.getMessage());

        } else {
          var request = sog.getRequest();
          reqArr.push(request.getDisplayName() + ": " + request.requested_for.getDisplayName());
        }        
      }

      obj.data = reqArr;
      outputs.request = reqArr;

    } catch (e) {
      gs.info("Bulk Order Guide Error - " + e);
    }
})(inputs, outputs);

